import styled, { createGlobalStyle } from 'styled-components';

export const GlobalStyle = createGlobalStyle`
    *{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    body, html{
        height: 100%;
        min-height: 100vh;
        background-color: #cecece;
    }
`;

export const Section = styled.div`
    height: 700px;
    width: 100%;
    background-color: aqua;
    text-align: center;
    /* margin: 0 auto; */
`;
