import React, { useState } from 'react';
import { ProdutoContext } from '../../context/produtoContext';

import { Container, Wrapper, OldPrice, NewPrice, Description, Button, Image, PartPrice, Favourite } from './styles';

function Carrocel({ 
    img, 
    oldPrice,
    newPrice,
    partPrice,
    description,
    key ,
}) {

    const [singleProduct, setSingleProduct] = useState({});

    function handleInfos(data){
        console.log(data);
        setSingleProduct(data);
    }

    return(
        <Container key={key}>
            <Wrapper>
                <Image src={img} alt="imagem"/>

                <OldPrice>
                {oldPrice}
                </OldPrice>

                <NewPrice>
                    {newPrice}
                </NewPrice>

                <PartPrice>
                    {partPrice}
                </PartPrice>

                <Description>
                    <p>
                        {description}
                    </p>
                    <Favourite />
                </Description>

                <Button onClick={() => handleInfos({
                    oldPrice: oldPrice,
                    newPrice: newPrice,
                    partPrice: partPrice,
                    description: description
                })}>
                    Comprar
                </Button>
            </Wrapper>
        </Container>
    );
}

export default Carrocel;
