import React, { useContext, useState } from 'react';
import { ProdutoContext } from '../../context/produtoContext';

import { Container, Wrapper, OldPrice, NewPrice, Description, Button, Image, PartPrice, Favourite } from './styles';

function Carrocel({ 
    img, 
    oldPrice,
    newPrice,
    partPrice,
    description,
    key ,
}) {

    const {handleInfos} = useContext(ProdutoContext);
    let [counter, setCounter] = useState(0);

    // const sumCountter = () => {
    //     setCounter()
    // }

    return(
        <Container key={key}>
            <Wrapper>
                <Image src={img} alt="imagem"/>

                <OldPrice>
                    {oldPrice}
                </OldPrice>

                <NewPrice>
                    {newPrice}
                </NewPrice>

                <PartPrice>
                    {partPrice}
                </PartPrice>

                <Description>
                    <p>
                        {description}
                    </p>
                    <Favourite />
                </Description>

                <Button onClick={() => 

                    handleInfos({
                        oldPrice: oldPrice,
                        newPrice: newPrice,
                        partPrice: partPrice,
                        description: description,
                        img: img,
                        counter: setCounter(counter + 1)  
                    })
                }>
                    Comprar
                </Button>
            </Wrapper>
        </Container>
    );
}

export default Carrocel;
