import React, { useState } from 'react';
import Carrocel from '../../components/Carrocel';
import SectionSlider from '../../components/Slider';

import { Container, CaroucelSection } from './styles';

import Slider from "react-slick";
import '../../styles/slick.css';
import { Section } from '../../styles/GlobalStyles';

import { carroucelInfo } from '../../assets/info';

import room from '../../image/royal-interior.jpg';

export function Home() {

  const [info] = useState(carroucelInfo);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1
  };
  return(
    <Container>
      <SectionSlider />

      <CaroucelSection>
        <Slider {...settings}>
          {info.map(caroucel => (

            <Carrocel 
              img={caroucel.img} 
              oldPrice={caroucel.oldPrice} 
              newPrice={caroucel.newPrice} 
              partPrice={caroucel.partPrice}
              description={caroucel.description}
              key={caroucel.id}  
            />

          ))}

        </Slider>
      </CaroucelSection>

      <Section style={{ background: 'var(--secondary)'}}>

          <img src={room} alt="room" />

      </Section>

      <Section style={{ background: 'white'}}>

      </Section>

    </Container>
  );
};
