import React, { useContext, useState } from 'react';
import { ProdutoContext } from '../../context/produtoContext';

import { 
    Container,
    CartTitle,
    LeftSideCart,
    RightSideCart,
    ProductInfoCart,
    SectionCart,
    InfosBoxes,
    DescriptionRadio,
    FinalizarBotao,
    Image,
    MessageEmpty
} from './styles';

function Cart() {

    const { products } = useContext(ProdutoContext);
    const [initalValue, setInitialValue] = useState(0);

  return(
        <Container>
            <CartTitle>Cart</CartTitle>
            <SectionCart>
                <LeftSideCart>
                    {products.length === 0  ? (
                        <>
                            <MessageEmpty>Não existem itens no carrinho</MessageEmpty>
                        </>
                    ) : (
                        <>
                        {products.map(singleProduct => (
                            <ProductInfoCart key={singleProduct.description}>
                                <li>
                                    <span>x</span>
                                    <Image src={singleProduct.img} alt="imagem"/>
                                    <span>{singleProduct.newPrice}</span>
                                    <span>{singleProduct.counter}</span>
                                </li>
                            </ProductInfoCart>
                        ))}
                        </>
                    )}
                </LeftSideCart>

                <RightSideCart>
                    <InfosBoxes>Subtotal <span>{products.map(singleProduct => ( setInitialValue(initalValue + singleProduct.newPrice)  ))}</span></InfosBoxes>

                    <InfosBoxes>
                        Subtotal 

                        <span>
                            <input type="radio" placeholder="asd"/><DescriptionRadio>Frete Grátis</DescriptionRadio><br />
                        </span>

                    </InfosBoxes>

                    <InfosBoxes>Total <span> R$ 220,00</span></InfosBoxes>

                    <FinalizarBotao>Finalizar</FinalizarBotao>
                </RightSideCart>

            </SectionCart>
        </Container>
    );
}

export default Cart;
